function gamma_a=gamma_cc(a, b, delta, gamma_front,R)
    sol_n = 1;
    syms z
    gamma_a=vpa((2*R^2*a^2 - a^4*cos(2*delta + 2*atan(gamma_front)) + 3*R^2*b^2 + a^2*b^2 - R^2*a^2*cos(2*delta + 2*atan(gamma_front)))/(a^2*sin(2*delta + 2*atan(gamma_front))*(R^2 - a^2)) - ((a - b)*root(2*a*b*z^4 - b^2*z^4 - a^2*z^4 - 4*R*a*b*z^3 + 4*R*b^2*z^3 + 2*a^2*b^2*z^2 - 6*R^2*b^2*z^2 + 2*R^2*a^2*z^2 - 2*a^4*z^2*cos(2*delta + 2*atan(gamma_front)) - 4*R*a^2*b^2*z + 4*R^3*a*b*z - 4*R*a^3*b*z + 4*R^3*b^2*z - 2*R^4*a*b + 2*R^2*a^2*b^2 + 4*R^2*a^3*b - a^4*b^2 - R^4*b^2 + 2*R^2*a^4 - R^4*a^2 - 2*a^5*b - a^6, z, sol_n)^2*(R^2*a^2 + 7*R^2*b^2 - a^4 + a^2*b^2))/(a^2*sin(2*delta + 2*atan(gamma_front))*(a + b)*(R^2 - a^2)^2) - (4*R*b*(R^2*b^2 - a^4*tan(delta + atan(gamma_front))^2 + R^2*b^2*tan(delta + atan(gamma_front))^2)*root(4*a*b*z^4 - 2*b^2*z^4 - 2*a^2*z^4 - 8*R*a*b*z^3 + 8*R*b^2*z^3 + 4*a^2*b^2*z^2 - 12*R^2*b^2*z^2 + 4*R^2*a^2*z^2 - 4*a^4*z^2*cos(2*delta + 2*atan(gamma_front)) - 8*R*a^2*b^2*z + 8*R^3*a*b*z - 8*R*a^3*b*z + 8*R^3*b^2*z - 4*R^4*a*b + 4*R^2*a^2*b^2 + 8*R^2*a^3*b - 2*a^4*b^2 - 2*R^4*b^2 + 4*R^2*a^4 - 2*R^4*a^2 - 4*a^5*b - 2*a^6, z, sol_n))/(tan(delta + atan(gamma_front))*(a + b)*(R^2 - a^2)^2*a^2) - (2*R*b*(a - b)^2*root(2*a*b*z^4 - b^2*z^4 - a^2*z^4 - 4*R*a*b*z^3 + 4*R*b^2*z^3 + 2*a^2*b^2*z^2 - 6*R^2*b^2*z^2 + 2*R^2*a^2*z^2 - 2*a^4*z^2*cos(2*delta + 2*atan(gamma_front)) - 4*R*a^2*b^2*z + 4*R^3*a*b*z - 4*R*a^3*b*z + 4*R^3*b^2*z - 2*R^4*a*b + 2*R^2*a^2*b^2 + 4*R^2*a^3*b - a^4*b^2 - R^4*b^2 + 2*R^2*a^4 - R^4*a^2 - 2*a^5*b - a^6, z, sol_n)^3)/(a^2*sin(2*delta + 2*atan(gamma_front))*(a + b)*(R^2 - a^2)^2));
    gamma_a=eval(gamma_a);
end